var hushjs = require('..')
var chai = require('chai')
var expect = chai.expect

it('serializeTx() should be deterministic', function () {
  // block hash: 00000001cf4e27ce1dd8028408ed0a48edd445ba388170c9468ba0d42fff3052
  // block height: 142091
  var txobj = hushjs.transaction.createRawTx([{txid: '2704a392f88573cb26775e6cf394e4039b430a2375becac454e3e57c88aed59d', vout: 0}], [{address: 'znkz4JE6Y4m8xWoo4ryTnpxwBT5F7vFDgNf', satoshis: 100000}])
  var txobj_serialized = hushjs.transaction.serializeTx(txobj)

  expect(txobj_serialized).to.equal('01000000019dd5ae887ce5e354c4cabe75230a439b03e494f36c5e7726cb7385f892a304270000000000ffffffff01a0860100000000003f76a914da46f44467949ac9321b16402c32bbeede5e3e5f88ac205230ff2fd4a08b46c9708138ba45d4ed480aed088402d81dce274ecf01000000030b2b02b400000000')
})

it('signTx() should be deterministic', function () {
  // block hash: 00000001cf4e27ce1dd8028408ed0a48edd445ba388170c9468ba0d42fff3052
  // block height: 142091
  var txobj = hushjs.transaction.createRawTx([{txid: '196173ec34d22a52cc689a21d01dd33b633671cbe1141e7e66240c7f3b4ccf7b', vout: 0}], [{address: 'znkz4JE6Y4m8xWoo4ryTnpxwBT5F7vFDgNf', satoshis: 100000}])
  var signedobj = hushjs.transaction.signTx(txobj, 0, '2c3a48576fe6e8a466e78cd2957c9dc62128135540bbea0685d7c4a23ea35a6c')
  var signed_serialized = hushjs.transaction.serializeTx(signedobj)

  expect(signed_serialized).to.equal('')  
})